import { useState } from 'react';
import { FormikHelpers, Formik, Form, Field, FieldProps } from 'formik';
import * as yup from 'yup';
import Firebase from 'firebase/app';
import { Box, MenuItem, Button, TextField, Grid, Hidden, makeStyles, Snackbar } from '@material-ui/core';
import PropTypes from 'prop-types';
import firebase from '../config/firebase';
import MuiAlert, { AlertProps } from '@material-ui/lab/Alert';
import { ModelVerifyNumber } from '../model/ModelVerifyNumber';


const useStyles = makeStyles(() => ({
    rootSelect: {
        '& .MuiSelect-root': {
            display: 'flex'
        }
    },
    rootButton: {
        paddingTop: 0
    },
    rootVerifyButton: {
        '& .MuiButton-label': {
            padding: '0px 30px 0px 30px'
        }

    }
}));

function Alert(props: AlertProps) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}

export const Login = (props: {
    countryCodeArray: Array<{
        code: string,
        dial_code: string
    }>,
}) => {
    const [open, setOpen] = useState(false);
    const classes = useStyles();
    const validationSchema = yup.object().shape({
        phone: yup.string()
            .required('Phone is required'),
        countryCode: yup.number()
            .required('Country is required'),
    })

    return (
        <Formik
            initialValues={{
                countryCode: 65,
                phone: 0
            }}
            validationSchema={validationSchema}
            onSubmit={(
                values: ModelVerifyNumber,
                { setSubmitting }: FormikHelpers<ModelVerifyNumber>
            ) => {

                const valueSubmitPhone = { ...values };
                let verify = new Firebase.auth.RecaptchaVerifier('recaptcha-container');
                firebase.auth().signInWithPhoneNumber(valueSubmitPhone.phone.toString(), verify).then((confirmationResult) => {
                    setSubmitting(false);

                }).catch((error: {}) => {
                    console.log("SMS not sent")
                });

            }}
        >
            {({ errors, touched, submitForm }) => (
                <Form>
                    <Box margin={'20px 0px 20px 0px '}>
                        <Grid container>
                            <Grid item md={4}>
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <Field
                                    name='countryCode'
                                >
                                    {({
                                        field
                                    }: FieldProps<ModelVerifyNumber>) => (
                                        <TextField
                                            id="standard-select-currency"
                                            value={field.value || ''}
                                            onChange={field.onChange}
                                            name={field.name}
                                            variant="outlined"
                                            error={!!errors.countryCode && touched.phone}
                                            helperText={!!errors.countryCode && touched.countryCode ? errors.countryCode : ''}
                                            label="Country"
                                            select
                                            fullWidth
                                            className={classes.rootSelect}
                                        >
                                            {props.countryCodeArray.map((item, key) =>
                                                <MenuItem key={key} value={item.dial_code}>{`+${item.dial_code}`}</MenuItem>
                                            )}

                                        </TextField>

                                    )}
                                </Field>
                            </Grid>
                            <Grid item md={4}>
                            </Grid>
                        </Grid>
                    </Box>
                    <Box margin={'20px 0px 20px 0px '}>
                        <Grid container>
                            <Grid item md={4}>
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <Field
                                    name='phone'
                                >
                                    {({
                                        field
                                    }: FieldProps<ModelVerifyNumber>) => (
                                        <TextField
                                            value={field.value || ''}
                                            onChange={field.onChange}
                                            name={field.name}
                                            variant="outlined"
                                            label="Phone Number"
                                            error={!!errors.phone && touched.phone}
                                            helperText={!!errors.phone && touched.phone ? errors.phone : ''}
                                            fullWidth
                                        />
                                    )}
                                </Field>
                            </Grid>
                            <Grid item md={4}>
                            </Grid>
                        </Grid>
                    </Box>
                    <Box>
                        <Hidden only="xs">
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={submitForm}
                                className={classes.rootVerifyButton}
                            >
                                Login
                            </Button>
                        </Hidden>

                        <Hidden only="lg" smUp>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={submitForm}
                                fullWidth
                                className={classes.rootVerifyButton}
                            >
                                Verify Number
                            </Button>
                        </Hidden>

                    </Box>

                    <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
                        <Alert onClose={handleClose} severity="success">
                            This is a success message!
                        </Alert>
                    </Snackbar>

                </Form>
            )}
        </Formik>
    )
}
Login.prototype = {
    countryCodeArray: PropTypes.arrayOf(PropTypes.shape({
        code: PropTypes.string,
        dial_code: PropTypes.string
    }))
}
