import { FormikHelpers, Formik, Form, Field, FieldProps } from 'formik';
import * as yup from 'yup';
import { makeStyles } from '@material-ui/core/styles';
import Firebase from 'firebase/app';
import { Box, MenuItem, Button, TextField, Grid, Hidden } from '@material-ui/core';
import { ModelVerifyNumber } from '../model/ModelVerifyNumber';
import firebase from '../../config/firebase';

const useStyles = makeStyles(() => ({
    rootSelect: {
        '& .MuiSelect-root' : {
            display: 'flex'
        }
    },
    rootButton : {
        paddingTop: 0
    }
}));
export const VerifyNumber = (props: {
    setValueSubmit: React.Dispatch<React.SetStateAction<ModelVerifyNumber>>,
    setfinal: React.Dispatch<React.SetStateAction<{}>>,
    setShowOTP: React.Dispatch<React.SetStateAction<boolean>>,
    countryCodeArray: Array<{
        code: string,
        dial_code: string
    }>
}) => {
    const validationSchema = yup.object().shape({
        phone: yup.string()
            .required('Phone is required'),
        country: yup.string()
            .required('Country is required'),
    })
    const classes = useStyles();

    return (
        <Formik
            initialValues={{
                country: '',
                phone: ''
            }}
            validationSchema={validationSchema}
            onSubmit={(
                values: ModelVerifyNumber,
                { setSubmitting }: FormikHelpers<ModelVerifyNumber>
            ) => {
                props.setValueSubmit({
                    country: '',
                    phone: values.phone
                })
                let verify = new Firebase.auth.RecaptchaVerifier('recaptcha-container');
                firebase.auth().signInWithPhoneNumber(values.phone, verify).then((confirmationResult) => {
                    console.log(Object.getPrototypeOf(confirmationResult).confirm(), 'confirmationResult')
                    console.log("OTP has been sent")
                    props.setfinal(confirmationResult);
                    setSubmitting(false);
                    props.setShowOTP(true)
                }).catch((error) => {
                    console.log("SMS not sent")
                });

            }}
        >
            {({ errors, touched, submitForm }) => (
                <Form>
                    <Box margin={'20px 0px 20px 0px '}>
                        <Grid container>
                            <Grid item md={4}>
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <Field
                                    name='country'
                                >
                                    {({
                                        field
                                    }: FieldProps<ModelVerifyNumber>) => (
                                        <TextField
                                            id="standard-select-currency"
                                            value={field.value || ''}
                                            onChange={field.onChange}
                                            name={field.name}
                                            variant="outlined"
                                            error={!!errors.country && touched.phone}
                                            helperText={!!errors.country && touched.country ? errors.country : ''}
                                            label="Country"
                                            select
                                            fullWidth
                                            className={classes.rootSelect}
                                        >
                                            {props.countryCodeArray.map((item, key) =>
                                                <MenuItem key={key} value={item.code}>{item.dial_code}</MenuItem>
                                            )}

                                        </TextField>

                                    )}
                                </Field>
                            </Grid>
                            <Grid item md={4}>
                            </Grid>
                        </Grid>
                    </Box>
                    <Box margin={'20px 0px 20px 0px '}>
                        <Grid container>
                            <Grid item md={4}>
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <Field
                                    name='phone'
                                >
                                    {({
                                        field
                                    }: FieldProps<ModelVerifyNumber>) => (
                                        <TextField
                                            value={field.value || ''}
                                            onChange={field.onChange}
                                            name={field.name}
                                            variant="outlined"
                                            label="Phone Number"
                                            error={!!errors.phone && touched.phone}
                                            helperText={!!errors.phone && touched.phone ? errors.phone : ''}
                                            fullWidth
                                        />
                                    )}
                                </Field>
                            </Grid>
                            <Grid item md={4}>
                            </Grid>
                        </Grid>
                    </Box>
                    <Box>
                        <Hidden only="xs">
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={submitForm}
                            >
                                Verify Number
                            </Button>
                        </Hidden>

                        <Hidden only="lg" smUp>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={submitForm}
                                fullWidth
                            >
                                Verify Number
                            </Button>
                        </Hidden>
                        <div id="recaptcha-container" />
                    </Box>

                    <Box marginTop="20px">
                        <Grid container>
                            <Grid item md={4}>
                            </Grid>
                            <Grid item xs={12} md={4}>
                                <Grid container>
                                    <Grid item xs={9} md={8}>
                                        <b>
                                            ALREADY HAVE AN ACCOUNT?
                                        </b>
                                    </Grid>
                                    <Hidden only="lg" smUp>
                                        <Grid item xs={1}>
                                        </Grid>
                                    </Hidden>

                                    <Grid item xs={2} md={4}>
                                        <Button color="primary" className={classes.rootButton}>
                                            LOG IN
                                        </Button>
                                    </Grid>
                                </Grid>
                            </Grid>
                            <Grid item md={4}>
                            </Grid>
                        </Grid>
                    </Box>
                </Form>
            )}
        </Formik>
    )
}
