import React from 'react';
import { Box, MenuItem, Typography, Button, TextField, InputAdornment, Grid, Hidden, Link } from '@material-ui/core';
import { FormikHelpers, Formik, Form, Field, FieldProps } from 'formik';
import * as yup from 'yup';
import { ModelOTP } from './model';

const validationSchema = yup.object({
  country: yup.string()
    .required('Field is required'),
  phone: yup.string()
    .required('Field is required')
})

const OTP = () => {
  return (
    <Box>
      <Box textAlign="left" padding="20px" marginTop="20px">
        <Typography variant="h4" component="h4" >DayOne</Typography>
      </Box>
      <Box padding={"20px"}>
        <Box>
          <Typography variant="h4" component="h4" >Let's get started!</Typography>
        </Box>
        <Box>
          <Formik
            initialValues={{
              country: '',
              phone: ''
            }}
            validationSchema={validationSchema}
            onSubmit={(
              values: ModelOTP,
              { setSubmitting }: FormikHelpers<ModelOTP>
            ) => {
              setTimeout(() => {
                console.log(values, 'value----')
                setSubmitting(false);
              }, 500);
            }}
          >
            {({ errors, touched, submitForm }) => (
              <Form>
                <Box margin={'20px 0px 20px 0px '}>
                  <Grid container>
                    <Grid item md={4}>
                    </Grid>
                    <Grid item xs={12} md={4}>
                      <Field
                        name='country'
                      >
                        {({
                          field
                        }: FieldProps<ModelOTP>) => (
                          <TextField
                            value={field.value || ''}
                            onChange={field.onChange}
                            name={field.name}
                            variant="outlined"
                            error={!!errors.country}
                            helperText={errors.country}
                            label="Country"
                            select
                            fullWidth
                            InputProps={{
                              startAdornment: <InputAdornment position="start">+65</InputAdornment>,
                            }}

                          >
                            {console.log('errors', errors)}
                            {console.log('touched', touched)}
                            <MenuItem value={10}>Ten</MenuItem>
                            <MenuItem value={20}>Twenty</MenuItem>
                            <MenuItem value={30}>Thirty</MenuItem>
                          </TextField>

                        )}
                      </Field>
                    </Grid>
                    <Grid item md={4}>
                    </Grid>
                  </Grid>
                </Box>
                <Box margin={'20px 0px 20px 0px '}>
                  <Grid container>
                    <Grid item md={4}>
                    </Grid>
                    <Grid item xs={12} md={4}>
                      <Field
                        name='phone'
                      >
                        {({
                          field
                        }: FieldProps<ModelOTP>) => (
                          <TextField
                            value={field.value || ''}
                            onChange={field.onChange}
                            name={field.name}
                            variant="outlined"
                            label="Phone Number"
                            error={!!errors.phone}
                            helperText={errors.phone}
                            fullWidth
                          />
                        )}
                      </Field>
                    </Grid>
                    <Grid item md={4}>
                    </Grid>
                  </Grid>
                </Box>
                <Box>
                  <Hidden only="xs">
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={submitForm}
                    >
                      Verify Number
                    </Button>
                  </Hidden>

                  <Hidden only="lg" smUp>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={submitForm}
                      fullWidth
                    >
                      Verify Number
                    </Button>
                  </Hidden>
                </Box>

                <Box marginTop="20px">
                  <Grid container>
                    <Grid item md={4}>
                    </Grid>
                    <Grid item xs={12} md={4}>
                      <Grid container>
                        <Grid item xs={9} md={8}>
                          <b>
                            ALREADY HAVE AN ACCOUNT?
                          </b>
                        </Grid>
                        <Hidden only="lg" smUp>
                          <Grid item xs={1}>
                          </Grid>
                        </Hidden>

                        <Grid item xs={2} md={4}>
                          <Link href="#" underline="none">
                            LOG IN
                          </Link>
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid item md={4}>
                    </Grid>
                  </Grid>
                </Box>

              </Form>
            )}
          </Formik>
        </Box>

      </Box>

    </Box >
  )
}

export default OTP;
